import java.util.Scanner;public class mathxy {public static void main(String[] args) {Scanner scanner=new Scanner(System.in);System.out.println("/ax + by = e [1]");System.out.println("/cx + dy = f [2]");System.out.println("a = ");float a=scanner.nextInt();System.out.println("b = ");float b=scanner.nextInt();System.out.println("c = ");float c=scanner.nextInt();System.out.println("d = ");float d=scanner.nextInt();System.out.println("e = ");float e=scanner.nextInt();System.out.println("f = ");float f=scanner.nextInt();float bm = Math.abs(b);float dm = Math.abs(d);if ((b>0)&&(d>0)) {float fir = (a*dm - c*bm);float sec = (e*dm-f*bm);float x = (e*dm-f*bm)/(a*dm - c*bm);float y = (e - a*x)/b;System.out.println("");System.out.println("/"+a+"x + "+b+"y = "+e+" [1]");System.out.println("/"+c+"x + "+d+"y = "+f+" [2]");System.out.println("solution: ");System.out.println("[1]*"+dm+": "+a*dm+"x + "+b*dm+"y = "+e*dm+" [3]");System.out.println("[2]*"+bm+": "+c*bm+"x + "+d*bm+"y = "+f*bm+" [4]");System.out.println("[3]-[4]: "+fir+"x = "+sec);System.out.println("       =>"+"x = "+x);System.out.println("x="+x+"=>[1]: "+a+"x + "+b+"y = "+e);System.out.println("            => "+a*x+" + "+b+"y = "+e);System.out.println("            => "+b+"y = "+y*b);System.out.println("            => y = "+y);System.out.println("result:");System.out.println("/x = "+x);System.out.println("/y = "+y);System.out.println("over");}if ((b<0)&&(d<0)) {float fir = (a*dm - c*bm);float sec = (e*dm-f*bm);float x = (e*dm-f*bm)/(a*dm - c*bm);float y = (e - a*x)/b;System.out.println("");System.out.println("/"+a+"x + ("+b+")y = "+e+" [1]");System.out.println("/"+c+"x + ("+d+")y = "+f+" [2]");System.out.println("solution: ");System.out.println("[1]*"+dm+": "+a*dm+"x + ("+b*dm+")y = "+e*dm+" [3]");System.out.println("[2]*"+bm+": "+c*bm+"x + ("+d*bm+")y = "+f*bm+" [4]");System.out.println("[3]-[4]: "+fir+"x = "+sec);System.out.println("       =>"+"x = "+x);System.out.println("x="+x+"=>[1]: "+a+"x + ("+b+")y = "+e);System.out.println("            => "+a*x+" + ("+b+")y = "+e);System.out.println("            => "+b+"y = "+y*b);System.out.println("            => y = "+y);System.out.println("result:");System.out.println("/x = "+x);System.out.println("/y = "+y);System.out.println("over");}if ((b<0)&&(d>0)) {float fir = (a*dm + c*bm);float sec = (e*dm+f*bm);float x = (e*dm+f*bm)/(a*dm + c*bm);float y = (e - a*x)/b;System.out.println("");System.out.println("/"+a+"x + ("+b+")y = "+e+" [1]");System.out.println("/"+c+"x + "+d+"y = "+f+" [2]");System.out.println("solution: ");System.out.println("[1]*"+dm+": "+a*dm+"x + ("+b*dm+")y = "+e*dm+" [3]");System.out.println("[2]*"+bm+": "+c*bm+"x + "+d*bm+"y = "+f*bm+" [4]");System.out.println("[3]+[4]: "+fir+"x = "+sec);System.out.println("       =>"+"x = "+x);System.out.println("x="+x+"=>[1]: "+a+"x + ("+b+")y = "+e);System.out.println("            => "+a*x+" + ("+b+")y = "+e);System.out.println("            => "+b+"y = "+y*b);System.out.println("            => y = "+y);System.out.println("result:");System.out.println("/x = "+x);System.out.println("/y = "+y);System.out.println("over");}if ((b>0)&&(d<0)) {float fir = (a*dm + c*bm);float sec = (e*dm+f*bm);float x = (e*dm+f*bm)/(a*dm + c*bm);float y = (e - a*x)/b;System.out.println("");System.out.println("/"+a+"x + "+b+"y = "+e+" [1]");System.out.println("/"+c+"x + ("+d+")y = "+f+" [2]");System.out.println("solution: ");System.out.println("[1]*"+dm+": "+a*dm+"x + "+b*dm+"y = "+e*dm+" [3]");System.out.println("[2]*"+bm+": "+c*bm+"x + ("+d*bm+")y = "+f*bm+" [4]");System.out.println("[3]+[4]: "+fir+"x = "+sec);System.out.println("       =>"+"x = "+x);System.out.println("x="+x+"=>[1]: "+a+"x + "+b+"y = "+e);System.out.println("            => "+a*x+" + "+b+"y = "+e);System.out.println("            => "+b+"y = "+y*b);System.out.println("            => y = "+y);System.out.println("result:");System.out.println("/x = "+x);System.out.println("/y = "+y);System.out.println("over");}}}
